mapper.select:
select count(member_id)
from member
where member_id = #{memberId}

select member_id, member_name
from member
where member_id = #{userId} and member_password = #{userPwd}

select member_id, member_name, member_password, email_id, email_domain, join_date
from member
where member_id = #{memberId}

mapper.update:
update member
member_name = #{memberName}, member_phone = #{memberPhone}, email_id = #{emailId}, email_domain = #{emailDomain}, birth = #{birth}, nickname = #{nickname}
where member_id = #{memberId}

update member
set token = #{token}
where member_id = #{memberId}

update member
set token = #{token, jdbcType=VARCHAR}
where member_id = #{userId}

mapper.select:
select count(member_id)
from member
where member_id = #{userId} and member_password = #{userPassword}

mapper.update:
update member
set member_password = #{userPassword}
where member_id = #{userId}

mapper.select:
select count(*)
from follow
where `from` = #{from} and `to` = #{to}
